import "./App.css";
import "./css/style.css";

import Navbar from "./components/Navbar";

import Landing from "./pages/Landing";
import Team from "./pages/Team";
import Resources from "./pages/Resources";
import Syllabus from "./pages/Syllabus";

// dark theme toggle stuff
import React, { useState } from 'react';
import { ThemeProvider } from 'styled-components';
import { lightTheme, darkTheme } from './components/Theme';
import { GlobalStyles } from './components/Global';
import Toggle from './components/Toggle';
import { useDarkMode } from './useDarkMode';

import { ReactComponent as MoonIcon } from './icons/moon.svg'; // <div>Icons made by <a href="https://www.flaticon.com/authors/smalllikeart" title="smalllikeart">smalllikeart</a> from <a href="https://www.flaticon.com/" title="Flaticon">www.flaticon.com</a></div>
import { ReactComponent as SunIcon } from './icons/sun.svg'; // <div>Icons made by <a href="https://www.flaticon.com/authors/freepik" title="Freepik">Freepik</a> from <a href="https://www.flaticon.com/" title="Flaticon">www.flaticon.com</a></div>

function App() {
  const [theme, toggleTheme] = useDarkMode();

  const themeMode = theme === 'light' ? lightTheme : darkTheme;


  return (
    <div className="App" id="home">
      <Navbar />
      <div className="container">
      <ThemeProvider theme={themeMode}><><GlobalStyles /><Toggle theme={theme} toggleTheme={toggleTheme} /> </> </ThemeProvider>
        <Landing />
        <Team />
        <Resources />
        <Syllabus />
      </div>
    </div>
  );
}

export default App;
